var validateConfig = require('./lib/validate-config');
var getCurrentStepForProject = require('./lib/get-current-step-for-project');
var generateAnswer = require('./lib/generate-answer');
var submitAnswer = require('./lib/submit-answer');
var handleApiResponse = require('./lib/handle-api-response');

var config = require('../../ducktypecoder');

validateConfig(config);

getCurrentStepForProject(config)
  .then(step => generateAnswer(config, step))
  .then(answer => submitAnswer(answer))
  .then(response => handleApiResponse(response));
